import React, { useState, useEffect } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import { getMyApprovedLeaves, getAllApprovedLeaves } from "../../services/api";

const getMonthString = (date) =>
  `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, "0")}`;

const CalendarWithToggle = ({ user }) => {
  const [mode, setMode] = useState("mine"); // "mine" or "team"
  const [myLeaves, setMyLeaves] = useState([]);
  const [allLeaves, setAllLeaves] = useState([]);
  const [activeMonth, setActiveMonth] = useState(new Date());

  useEffect(() => {
    const fetchData = async () => {
      const month = getMonthString(activeMonth);
      if (mode === "mine") {
        const res = await getMyApprovedLeaves(month);
        setMyLeaves(res.data);
      } else {
        const res = await getAllApprovedLeaves(month);
        setAllLeaves(res.data);
      }
    };
    fetchData();
  }, [mode, activeMonth]);

  // Helper to get all days covered by a leave
  const getLeaveDays = (leave) => {
    const days = [];
    let current = new Date(leave.startDate);
    const end = new Date(leave.endDate);
    while (current <= end) {
      days.push(current.toDateString());
      current.setDate(current.getDate() + 1);
    }
    return days;
  };

  // For "mine" mode: days the user is on leave
  const myLeaveDays = new Set(myLeaves.flatMap(getLeaveDays));

  // For "team" mode: map of dateString -> count
  const teamLeaveCounts = {};
  allLeaves.forEach((leave) => {
    getLeaveDays(leave).forEach((day) => {
      teamLeaveCounts[day] = (teamLeaveCounts[day] || 0) + 1;
    });
  });

  // --- THE KEY PARTS ---
  const tileClassName = ({ date, view }) => {
    if (view === "month" && mode === "mine" && myLeaveDays.has(date.toDateString())) {
      return "my-leave-day";
    }
    return null;
  };

  const tileContent = ({ date, view }) => {
    if (view !== "month") return null;
    if (mode === "team" && teamLeaveCounts[date.toDateString()]) {
      // Red number for team leave count
      return (
        <span style={{
          position: "absolute",
          top: 2,
          right: 2,
          color: "red",
          fontWeight: "bold",
          fontSize: 14,
          zIndex: 2
        }}>
          {teamLeaveCounts[date.toDateString()]}
        </span>
      );
    }
    return null;
  };

  return (
    <div style={{ minWidth: 350, background: "#f9f9f9", borderRadius: 10, padding: 16 }}>
      <div style={{ display: "flex", alignItems: "center", marginBottom: 12 }}>
        <span style={{ fontWeight: "bold", fontSize: 22, color: "#2345a7", marginRight: 12 }}>
          Team Calendar
        </span>
        <label style={{ marginLeft: "auto", display: "flex", alignItems: "center" }}>
          <span style={{ marginRight: 8 }}>My Leaves</span>
          <input
            type="checkbox"
            checked={mode === "team"}
            onChange={() => setMode(mode === "mine" ? "team" : "mine")}
            style={{ width: 36, height: 20 }}
          />
          <span style={{ marginLeft: 8 }}>Team</span>
        </label>
      </div>
      <Calendar
        value={activeMonth}
        onActiveStartDateChange={({ activeStartDate }) => setActiveMonth(activeStartDate)}
        tileClassName={tileClassName}
        tileContent={tileContent}
      />
      <div style={{ marginTop: 12, fontSize: 14, color: "#888" }}>
        {mode === "mine"
          ? "Circled days: Your approved leaves"
          : "Red number: Number of employees on leave"}
      </div>
      {/* Add the custom CSS below to your main CSS file */}
      <style>
        {`
          .my-leave-day abbr {
            border: 2px solid #2345a7;
            border-radius: 50%;
            padding: 6px 10px;
            color: #2345a7;
            background: #e6f0ff;
            font-weight: bold;
            display: inline-block;
            min-width: 28px;
            min-height: 28px;
            text-align: center;
          }
        `}
      </style>
    </div>
  );
};

export default CalendarWithToggle;
