import React, { useEffect, useState } from "react";
import { getPendingLeaves, updateLeaveStatus } from "../services/api";

const AdminDashboard = () => {
  const [pendingLeaves, setPendingLeaves] = useState([]);

  const fetchPendingLeaves = async () => {
    const res = await getPendingLeaves();
    setPendingLeaves(res.data);
  };

  useEffect(() => {
    fetchPendingLeaves();
  }, []);

  const handleAction = async (leaveId, status) => {
    await updateLeaveStatus(leaveId, status);
    fetchPendingLeaves(); // refresh
  };

  return (
    <div>
      <h1>Leave Management System - Admin</h1>
      {pendingLeaves.map(leave => (
        <div key={leave._id}>
          <div>
            <strong>{leave.userId.name}</strong> - {leave.leaveType} ({leave.startDate} to {leave.endDate})
          </div>
          <div>Reason: {leave.reason}</div>
          <button onClick={() => handleAction(leave._id, "approved")}>Approve</button>
          <button onClick={() => handleAction(leave._id, "denied")}>Reject</button>
        </div>
      ))}
    </div>
  );
};

export default AdminDashboard;
