// components/Admin/AddHolidayModal.jsx
import React, { useState } from 'react';
import { addHoliday } from '../../services/api';

const AddHolidayModal = ({ onClose, onHolidayAdded }) => {
  const [date, setDate] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!date || !description) {
      setError('Please fill all fields');
      return;
    }
    try {
      await addHoliday({ date, description });
      onHolidayAdded();
      onClose();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to add holiday');
    }
  };

  return (
    <div className="modal">
      <form onSubmit={handleSubmit}>
        <h2>Add Holiday</h2>
        {error && <p className="error">{error}</p>}
        <label>Date:</label>
        <input type="date" value={date} onChange={e => setDate(e.target.value)} />
        <label>Description:</label>
        <input type="text" value={description} onChange={e => setDescription(e.target.value)} />
        <button type="submit">Add</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
};

export default AddHolidayModal;
