import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import { getPendingLeaves, updateLeaveStatus } from '../services/api';
import { useAuth } from '../context/AuthContext';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [pendingLeaves, setPendingLeaves] = useState([]);
  const [approvalStatus, setApprovalStatus] = useState({});
  const { logout } = useAuth();

  useEffect(() => {
    fetchPendingLeaves();
  }, []);

  const fetchPendingLeaves = async () => {
    try {
      const response = await getPendingLeaves();
      setPendingLeaves(response.data);
      
      // Initialize approval status for each leave
      const initialStatus = {};
      response.data.forEach(leave => {
        initialStatus[leave._id] = '';
      });
      setApprovalStatus(initialStatus);
    } catch (error) {
      toast.error('Failed to fetch pending leaves');
    }
  };

  const handleApprovalChange = (leaveId, status) => {
    setApprovalStatus(prev => ({
      ...prev,
      [leaveId]: status
    }));
  };

  const handleApprovalSubmit = async (leaveId) => {
    const status = approvalStatus[leaveId];
    if (!status) {
      toast.error('Please select approval status');
      return;
    }

    try {
      await updateLeaveStatus(leaveId, status);
      toast.success(`Leave ${status} successfully`);
      fetchPendingLeaves(); // Refresh the list
    } catch (error) {
      toast.error('Failed to update leave status');
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  const getLeaveTypeName = (leaveType) => {
    switch(leaveType) {
      case 'earnedLeave': return 'Earned leave';
      case 'sickLeave': return 'Sick leave';
      case 'casualLeave': return 'Casual leave';
      default: return leaveType;
    }
  };

  return (
    <div className="admin-dashboard">
      <div className="header">
        <h1>Leave Management System</h1>
        <div className="header-right">
          <span className="admin-label">Admin</span>
          <button onClick={logout} className="logout-btn">Logout</button>
        </div>
      </div>

      <div className="pending-leaves">
        {pendingLeaves.length > 0 ? (
          pendingLeaves.map((leave) => (
            <div key={leave._id} className="leave-request">
              <div className="employee-info">
                <div className="info-group">
                  <label>Emp name</label>
                  <div className="info-value">{leave.userId.name}</div>
                </div>
                
                <div className="info-group">
                  <label>Type of leave</label>
                  <div className="info-value">{getLeaveTypeName(leave.leaveType)}</div>
                </div>
                
                <div className="info-group approval-section">
                  <label>Approval</label>
                  <div className="approval-options">
                    <div className="radio-group">
                      <input 
                        type="radio" 
                        id={`yes-${leave._id}`}
                        name={`approval-${leave._id}`}
                        value="approved"
                        checked={approvalStatus[leave._id] === 'approved'}
                        onChange={() => handleApprovalChange(leave._id, 'approved')}
                      />
                      <label htmlFor={`yes-${leave._id}`}>Yes</label>
                    </div>
                    <div className="radio-group">
                      <input 
                        type="radio" 
                        id={`no-${leave._id}`}
                        name={`approval-${leave._id}`}
                        value="rejected"
                        checked={approvalStatus[leave._id] === 'rejected'}
                        onChange={() => handleApprovalChange(leave._id, 'rejected')}
                      />
                      <label htmlFor={`no-${leave._id}`}>No</label>
                    </div>
                  </div>
                </div>
              </div>

              <div className="leave-details">
                <div className="detail-group">
                  <label>Reason:</label>
                  <div className="detail-value">{leave.reason}</div>
                </div>
                
                <div className="detail-group">
                  <label>Date:-</label>
                  <div className="detail-value">
                    {formatDate(leave.startDate)}
                    {leave.endDate !== leave.startDate && ` to ${formatDate(leave.endDate)}`}
                    {leave.session !== 'fullDay' && ` (${leave.session})`}
                  </div>
                </div>
              </div>

              <button 
                className="approve-btn"
                onClick={() => handleApprovalSubmit(leave._id)}
                disabled={!approvalStatus[leave._id]}
              >
                Submit Decision
              </button>
            </div>
          ))
        ) : (
          <div className="no-leaves">No pending leave requests</div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
