import React, { useState } from "react";
import axios from "axios";

const API_URL = "http://localhost:5000/api/auth/login"; // Change if your backend runs elsewhere

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [token, setToken] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    try {
      const response = await axios.post(API_URL, { email, password });
      setMessage("Login successful!");
      setToken(response.data.token);
      // Optionally: Save token to localStorage
      // localStorage.setItem("token", response.data.token);
    } catch (error) {
      setMessage(
        error.response?.data?.message ||
        "Login failed. Please check your credentials."
      );
      setToken(null);
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: "60px auto", padding: 32, border: "1px solid #ccc", borderRadius: 8 }}>
      <h2>Login</h2>
      {message && (
        <div
          style={{
            background: token ? "#d4edda" : "#f8d7da",
            color: token ? "#155724" : "#721c24",
            padding: 10,
            marginBottom: 16,
            borderRadius: 4,
          }}
        >
          {message}
        </div>
      )}
      <form onSubmit={handleSubmit} autoComplete="on">
        <div style={{ marginBottom: 16 }}>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            autoComplete="username"
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{ width: "100%", padding: 8, marginTop: 4 }}
          />
        </div>
        <div style={{ marginBottom: 16 }}>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            autoComplete="current-password"
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ width: "100%", padding: 8, marginTop: 4 }}
          />
        </div>
        <button type="submit" style={{ width: "100%", padding: 10 }}>
          Login
        </button>
      </form>
      <div style={{ marginTop: 24 }}>
        <strong>Demo Credentials:</strong>
        <div>Employee: <code>employee@test.com</code> / <code>password123</code></div>
        <div>Admin: <code>admin@test.com</code> / <code>password123</code></div>
      </div>
      {token && (
        <div style={{ marginTop: 16, wordBreak: "break-all" }}>
          <strong>Your Token:</strong>
          <div style={{ fontSize: 12 }}>{token}</div>
        </div>
      )}
    </div>
  );
};

export default Login;
